import { useEffect } from 'react';
import { withRouter } from 'react-router-dom';

function ScrollToTop({ }) {
  useEffect(() => {
   
        window.scrollTo({
            left:0,
            top: 0,
            behavior: "smooth",
          });
   
  }, []);

  return (null);
}

export default withRouter(ScrollToTop);
