import React, { useState } from "react";

function Collaps(props) {
  const [buttonText, setButtonText] = useState('Rozwiń');

  function handleClick() {
    setButtonText(!active);
  }

  const [isOpen, setIsOpen] = useState(false);
  return (
    <div className="collaps">
        <div className="menu">
        <div className="icon"><img src={props.icon} alt="icons"></img></div>
      <div className="title">{props.label}</div>
        </div>
      
      <button className="toggle" onClick={() => {setIsOpen(!isOpen);handleClick(setButtonText ? "Rozwiń" : "Zwiń")}}>
       {buttonText}
      </button>
      <div className={isOpen ? "content-parent show" : "content-parent"}>
      <div className={"content"}>{props.children}</div>
    </div>
    </div>
  );
}

export default Collaps;
